import React, { useState, useEffect } from "react";
import margheritaImg from "../images/margherita.jpeg";
import pepperoniImg from "../images/pepperoni-caroussell.jpeg";
import chickenImg from "../images/chicken.avif";
import veggieImg from "../images/veggie.jpg";
import hawaiianImg from "../images/hawaiian.jpg";
import meatLoversImg from "../images/meat-lovers.jpg";
import { Mail, Phone, MapPin, CheckCircle, AlertCircle } from "lucide-react";

function App() {
  const [currentPizza, setCurrentPizza] = useState(0);
  const pizzas = [
    {
      name: "Margherita",
      description: "Classic tomato sauce, mozzarella & basil",
      image: margheritaImg,
    },
    {
      name: "Pepperoni",
      description: "Tomato sauce, mozzarella & spicy pepperoni",
      image: pepperoniImg,
    },
    {
      name: "BBQ Chicken",
      description: "BBQ sauce, chicken, red onions & cilantro",
      image: chickenImg,
    },
    {
      name: "Vegetarian",
      description: "Tomato sauce, veggies, mushrooms & olives",
      image: veggieImg,
    },
    {
      name: "Hawaiian",
      description: "Tomato sauce, ham, pineapple & mozzarella",
      image: hawaiianImg,
    },
    {
      name: "Meat Lovers",
      description: "Tomato sauce, pepperoni, sausage, bacon & ham",
      image: meatLoversImg,
    },
  ];

  // Auto-rotate carousel
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentPizza((prevPizza) => (prevPizza + 1) % pizzas.length);
    }, 3000);

    return () => clearInterval(timer);
  }, [pizzas.length]);

  const nextPizza = () => {
    setCurrentPizza((prevPizza) => (prevPizza + 1) % pizzas.length);
  };

  const prevPizza = () => {
    setCurrentPizza(
      (prevPizza) => (prevPizza - 1 + pizzas.length) % pizzas.length
    );
  };

  // Contact form

  // Form state
  const [formValues, setFormValues] = useState({
    name: "",
    email: "",
    message: "",
  });

  // Validation state
  const [errors, setErrors] = useState({});

  // Submission state
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({
      ...formValues,
      [name]: value,
    });

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors({
        ...errors,
        [name]: null,
      });
    }
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    // Name validation
    if (!formValues.name.trim()) {
      newErrors.name = "Name is required";
    }

    // Email validation
    if (!formValues.email.trim()) {
      newErrors.email = "Email is required";
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formValues.email)) {
      newErrors.email = "Please enter a valid email address";
    }

    // Message validation
    if (!formValues.message.trim()) {
      newErrors.message = "Message is required";
    } else if (formValues.message.trim().length < 10) {
      newErrors.message = "Message must be at least 10 characters";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      setIsSubmitting(true);

      // Simulate API call
      setTimeout(() => {
        setIsSubmitting(false);
        setIsSubmitted(true);

        // Reset form after 3 seconds
        setTimeout(() => {
          setIsSubmitted(false);
          setFormValues({
            name: "",
            email: "",
            message: "",
          });
        }, 3000);
      }, 1000);
    }
  };

  const additionalStyles = `
    .error-message {
      color: #ff3333;
      font-size: 14px;
      margin-top: 5px;
      display: flex;
      align-items: center;
    }

    .error-icon {
      width: 16px;
      height: 16px;
      margin-right: 5px;
      background-image: url("https://cdn.jsdelivr.net/npm/bootstrap-icons@1.8.0/icons/exclamation-circle-fill.svg");
      background-size: contain;
      background-repeat: no-repeat;
    }

    .input-error {
      border-color: #ff3333;
    }

    .success-message {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      height: 100%;
      text-align: center;
      padding: 30px;
    }

    .success-icon {
      width: 60px;
      height: 60px;
      background-image: url("https://cdn.jsdelivr.net/npm/bootstrap-icons@1.8.0/icons/check-circle-fill.svg");
      background-size: contain;
      background-repeat: no-repeat;
      margin-bottom: 20px;
    }

    .success-title {
      color: #28a745;
      font-size: 24px;
      font-weight: bold;
      margin-bottom: 10px;
    }

    .success-text {
      color: #666;
    }

    .form-button:disabled {
      background-color: #ffadadad;
      cursor: not-allowed;
    }

    .contact-info-box {
      background-color: #fff;
      border-radius: 8px;
      padding: 30px;
      box-shadow: 0 4px 10px rgba(0, 0, 0, 0.05);
    }

    .contact-form-box {
      background-color: #fff;
      border-radius: 8px;
      padding: 30px;
      box-shadow: 0 4px 10px rgba(0, 0, 0, 0.05);
    }

    .contact-title {
      font-size: 22px;
      font-weight: 600;
      margin-bottom: 25px;
    }

    .business-hours {
      margin-top: 25px;
      padding: 15px;
      background-color: #f8f9fa;
      border-radius: 5px;
      border: 1px solid #e9ecef;
    }

    .business-hours p {
      font-size: 14px;
      color: #495057;
    }

    .mail-icon {
      background-image: url("https://cdn.jsdelivr.net/npm/bootstrap-icons@1.8.0/icons/envelope-fill.svg");
    }

    .form-label {
      font-weight: 500;
      margin-bottom: 5px;
      display: block;
    }
  `;
  return (
    <div className="app-container">
      <header className="header">
        <div className="logo">
          <span className="logo-text">PizzaLand</span>
        </div>
        <nav className="nav">
          <a href="#menu" className="nav-link">
            Menu
          </a>
          <a href="#hours" className="nav-link">
            Hours
          </a>
          <a href="#contact" className="nav-link">
            Contact
          </a>
        </nav>
      </header>

      <section className="hero">
        <h1 className="hero-title">Welcome to PizzaLand</h1>
        <p className="hero-subtitle">Crafting Perfect Pizzas Since 2010</p>
        <button className="order-button">Order Now</button>
      </section>

      <section id="menu" className="menu-section">
        <h2 className="section-title">Our Specialty Pizzas</h2>

        <div className="carousel-container">
          <button className="carousel-button prev" onClick={prevPizza}>
            &#10094;
          </button>
          <div className="carousel">
            <div className="carousel-item">
              <h3 className="pizza-name">{pizzas[currentPizza].name}</h3>
              <img
                src={pizzas[currentPizza].image}
                alt={pizzas[currentPizza].name}
                className="pizza-image"
              />
              <p className="pizza-desc">{pizzas[currentPizza].description}</p>
            </div>
          </div>
          <button className="carousel-button next" onClick={nextPizza}>
            &#10095;
          </button>

          <div className="carousel-dots">
            {pizzas.map((_, index) => (
              <span
                key={index}
                className={`dot ${index === currentPizza ? "active" : ""}`}
                onClick={() => setCurrentPizza(index)}
              ></span>
            ))}
          </div>
        </div>

        <div className="menu-grid">
          {pizzas.map((pizza, index) => (
            <div className="menu-item" key={index}>
              <h3>{pizza.name}</h3>
              <p>{pizza.description}</p>
            </div>
          ))}
        </div>
      </section>

      <section id="hours" className="hours-section">
        <h2 className="section-title">Opening Hours</h2>
        <div className="hours-container">
          <div className="hours-item">
            <span className="day">Monday - Friday</span>
            <span className="time">11 AM - 10 PM</span>
          </div>
          <div className="hours-item">
            <span className="day">Saturday</span>
            <span className="time">12 PM - 11 PM</span>
          </div>
          <div className="hours-item">
            <span className="day">Sunday</span>
            <span className="time">Closed</span>
          </div>
        </div>
      </section>

      <section id="contact" className="bg-gray-50 py-16">
        <div className="max-w-5xl mx-auto px-4">
          <h2 className="text-3xl font-bold text-center mb-12 text-gray-800">
            Contact Us
          </h2>

          <div className="grid md:grid-cols-2 gap-8">
            {/* Contact Information */}
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h3 className="text-xl font-semibold mb-6 text-gray-700">
                Get in Touch
              </h3>

              <div className="space-y-4">
                <div className="flex items-center">
                  <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center mr-4">
                    <Phone className="text-blue-600" size={20} />
                  </div>
                  <span className="text-gray-700">+5544332211</span>
                </div>

                <div className="flex items-center">
                  <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center mr-4">
                    <Mail className="text-blue-600" size={20} />
                  </div>
                  <span className="text-gray-700">info@pizzaplace.com</span>
                </div>

                <div className="flex items-center">
                  <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center mr-4">
                    <MapPin className="text-blue-600" size={20} />
                  </div>
                  <span className="text-gray-700">123 Pizza St, Food City</span>
                </div>
              </div>

              <div className="mt-8 p-4 bg-blue-50 rounded-lg border border-blue-100">
                <p className="text-sm text-blue-700">
                  We're open from Monday to Sunday, 11:00 AM - 10:00 PM
                </p>
              </div>
            </div>

            {/* Contact Form */}
            <div className="bg-white p-6 rounded-lg shadow-md">
              {isSubmitted ? (
                <div className="h-full flex flex-col items-center justify-center text-center p-6">
                  <div className="w-16 h-16 rounded-full bg-green-100 flex items-center justify-center mb-4">
                    <CheckCircle className="text-green-600" size={32} />
                  </div>
                  <h3 className="text-2xl font-bold text-green-600 mb-2">
                    Message Sent!
                  </h3>
                  <p className="text-gray-600">
                    Thank you for contacting us. We'll get back to you shortly.
                  </p>
                </div>
              ) : (
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <label
                      htmlFor="name"
                      className="block text-sm font-medium text-gray-700 mb-1"
                    >
                      Name
                    </label>
                    <input
                      type="text"
                      id="name"
                      name="name"
                      value={formValues.name}
                      onChange={handleChange}
                      className={`w-full p-3 border rounded-md focus:ring-2 focus:outline-none ${
                        errors.name
                          ? "border-red-500 focus:ring-red-200"
                          : "border-gray-300 focus:ring-blue-200"
                      }`}
                      placeholder="Your name"
                    />
                    {errors.name && (
                      <p className="mt-1 text-sm text-red-600 flex items-center">
                        <AlertCircle size={14} className="mr-1" /> {errors.name}
                      </p>
                    )}
                  </div>

                  <div>
                    <label
                      htmlFor="email"
                      className="block text-sm font-medium text-gray-700 mb-1"
                    >
                      Email
                    </label>
                    <input
                      type="email"
                      id="email"
                      name="email"
                      value={formValues.email}
                      onChange={handleChange}
                      className={`w-full p-3 border rounded-md focus:ring-2 focus:outline-none ${
                        errors.email
                          ? "border-red-500 focus:ring-red-200"
                          : "border-gray-300 focus:ring-blue-200"
                      }`}
                      placeholder="your.email@example.com"
                    />
                    {errors.email && (
                      <p className="mt-1 text-sm text-red-600 flex items-center">
                        <AlertCircle size={14} className="mr-1" />{" "}
                        {errors.email}
                      </p>
                    )}
                  </div>

                  <div>
                    <label
                      htmlFor="message"
                      className="block text-sm font-medium text-gray-700 mb-1"
                    >
                      Message
                    </label>
                    <textarea
                      id="message"
                      name="message"
                      value={formValues.message}
                      onChange={handleChange}
                      rows="4"
                      className={`w-full p-3 border rounded-md focus:ring-2 focus:outline-none ${
                        errors.message
                          ? "border-red-500 focus:ring-red-200"
                          : "border-gray-300 focus:ring-blue-200"
                      }`}
                      placeholder="How can we help you?"
                    ></textarea>
                    {errors.message && (
                      <p className="mt-1 text-sm text-red-600 flex items-center">
                        <AlertCircle size={14} className="mr-1" />{" "}
                        {errors.message}
                      </p>
                    )}
                  </div>

                  <button
                    type="submit"
                    disabled={isSubmitting}
                    className={`w-full py-3 px-4 rounded-md text-white font-medium transition duration-200 ${
                      isSubmitting
                        ? "bg-blue-400 cursor-not-allowed"
                        : "bg-blue-600 hover:bg-blue-700"
                    }`}
                  >
                    {isSubmitting ? "Sending..." : "Send Message"}
                  </button>
                </form>
              )}
            </div>
          </div>
        </div>
      </section>

      <footer className="footer">
        <div className="social-links">
          <a href="#" className="social-link">
            <i className="icon facebook-icon"></i>
          </a>
          <a href="#" className="social-link">
            <i className="icon instagram-icon"></i>
          </a>
          <a href="#" className="social-link">
            <i className="icon twitter-icon"></i>
          </a>
        </div>
        <p className="copyright">© 2025 PizzaLand. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
